import React, { useEffect } from 'react';
import CardList from '../components/cardList';
import client from '../config';
import styled from 'styled-components';
import Paginator from '../components/paginator';

const calculateRange = (length) => Array.from({ length }, (v, k) => k + 1);

function Home(props) {

    const total = props.total;

    const limit = props.limit;
    const rangeLimit = Math.ceil(total / limit);
    const range = calculateRange(rangeLimit);

    const [page, updatePage] = useState(!!props.page ? props.page : 1);
    const [tag, updateTag] = useState('');

    useEffect(() => {
        void router.push({ pathname: '/', query: { page: page, tag: tag } });
    }, [page, tag]);

    const handleTagChosen = (tag) => {
        updatePage(1);
        updateTag(tag);
    };
    useEffect(() => {
        var grid = document.querySelector('.grid');
        var msnry = new Masonry(grid, {
            itemSelector: '.grid-item'
        });
    }, [])
    return (
        <>
            <CardContainer className="grid">
                {props.items.map(item => (
                    <CardList item={item} key={item.sys.id} />
                ))}
            </CardContainer>
            <div className="pagination">
                <Paginator handlePaginationChange={(event) => updatePage(event)} range={range} skip={page} />
            </div>
        </>
    )
}

Home.getInitialProps = async ({ query }) => {
    let page = 1;

    if (query.page) {
        page = parseInt(query.page + '');
    }
    const limit = 2;

    const { entries, total, skip } = await client.getEntries({
        skip: (page - 1) * limit,
        limit,
    });
    return { page, entries, total, skip, limit };
}

export default Home;

const CardContainer = styled.div`
    padding:2em 0 1em;
    @media (max-width: 968px) {
        padding:0.5em 0;
    }
`
