import styled from 'styled-components';

const CardList = ({ item }) => {
    console.log(item)
    const { title, summary, featuredImage } = item.fields;
    const { updatedAt } = item.sys;

    const featuredImageUrl = featuredImage && featuredImage.fields.file.url;
    const formatDate = date => {
        const stringDate = new Date(date);
        const year = stringDate.getFullYear();
        const month = stringDate.getMonth();
        const day = stringDate.getDate();

        return (year + '년 ' + month + '월 ' + day + '일');
    }

    return (
        <Card>
            <div className="card-wrapper">
                {featuredImage && <a href="#" className="feature-img" style={{ backgroundImage: "url(" + featuredImageUrl + ")" }}></a>}
                <a href="#" className="content">
                    <h4 className="title">{title}</h4>
                    <p className="summary">{summary}</p>
                </a>
                <p>{formatDate(updatedAt)}</p>
            </div>
        </Card>
    )
}

export default CardList;

const Card = styled.div`
    float:left;
    width:33.333%;
    box-sizing:border-box;
    padding:0.6em;

    .card-wrapper{
        display:block;
        height:250px;
        background:#fff;
        border-radius:2px;
        border:1px solid #eee;
        box-shadow:3px 3px 10px rgba(0,0,0,0.015);

        .feature-img{
            width:100%;
            padding-top:52%;
            position:relative;
            display:block;
            background-position:cover;


        }
    }
`
