import CodeBlock from '../components/codeRender';
import ReactMarkdown from "react-markdown";
import styled from 'styled-components';
import formatDate from '../components/renderDate';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const Post = ({ pageContent }) => {
    const { title, summary, featuredImage, tags } = pageContent.fields;
    return (
        <div>
            <h2>{pageContent.fields.title}</h2>
            <div className="meta">
                {tags &&
                    <div className="tags">
                        <TagIcon />
                        <TagsList>
                            {tags.map((tag, index) => <li key={index}>{tag}</li>)}
                        </TagsList>
                    </div>
                }
                <div className="date"><ClockIcon /> {formatDate(updatedAt)}</div>
            </div>

            <div className="post-body">
                <ReactMarkdown source={pageContent.fields.postbody} renderers={{ code: CodeBlock }} />
            </div>
        </div>
    )
}

export default Post;


const TagsList = styled.ul`
    display:inline-block;
    padding:1.2em 0.6em;
    li{
        display:inline-block;
        margin-right:0.6em;
        color:#8794a3;
    }
`
const TagIcon = styled(FontAwesomeIcon).attrs({
    icon: "tag", color: "#8794a3"
})`
    display:inline-block;
    margin:0 0.2em 0 0.4em;
    opacity:0.5;
`
const ClockIcon = styled(FontAwesomeIcon).attrs({
    icon: "clock", color: "#8794a3"
})`
    display:inline-block;
    margin:0 0.6em 0 0.4em;
    opacity:0.5;
`
