import { createClient } from 'contentful';
import getArticle from "../utils/contentful.js";

const client = createClient({
    space: 'szikisnpiddf',
    accessToken: 'NHo2ByFSfsVvVZpuNEsqdr7aC53bcsA89jOGGzSLYKk'
})

const Home = (props) => {
    return (
        <div>
            <h1>{props.article.title}</h1>
            <div>{props.article.body}</div>
        </div>
    )
}
Home.getInitialProps = async () => {
    const contentfulArticle = await getArticle("article");
    const article = contentfulArticle && contentfulArticle.fields;
    return { article };
}


export default Home;
