import { documentToHtmlString } from '@contentful/rich-text-html-renderer';
import { documentToReactComponents } from '@contentful/rich-text-react-renderer';
const Post = ({ pageContent }) => {

    // const richContent = pageContent.fields.postbody;
    // const renderedContent = documentToHtmlString(richContent)
    console.log(pageContent);
    const body = marked(pageContent.fields.testbody || "");
    return (
        <div>
            <p>{pageContent.fields.title}</p>
            <div id="rich-text-body" className="post-body">
                {/* {documentToReactComponents(richContent)} */}
                {
                    <div dangerouslySetInnerHTML={{ __html: body }}></div>
                }
            </div>
        </div>
    )
}

export default Post;
