import getArticle from "../utils/contentful.js";

function Home(props) {
    return (
        <div>
            {props.article}
        </div>
    )
}
Home.getInitialProps = async () => {
    const contentfulArticle = await getArticle("blogPost");
    const article = contentfulArticle.items;
    //console.log(article)

    return {
        article
    };
}


export default Home;
