const cardList = ({ item }) => {

    const { tittle, summary } = item.fields;
    const { updatedAt } = item.sys;

    const formatDate = date => new Date(date).toDateString();

    return (
        <div>
            <p>{title}</p>
            <p>{summary}</p>
            <p>{formatDate(updatedAt)}</p>
        </div>
    )
}

export default cardList;
