import Link from 'next/link';
import styled from 'styled-components';
import formatDate from '../components/renderDate';

const PostList = ({ item }) => {
    const { title, summary, featuredImage } = item.fields;
    const { updatedAt, id, contentType } = item.sys;

    const postType = contentType.sys.id;
    const pageType = postType.replace('Post', '');

    const featuredImageUrl = featuredImage && featuredImage.fields.file.url;
    return (
        <PostItem>
            <div className="post-item-wrapper">
                {featuredImage && <Link href={`/${pageType}/[id]`} as={`/${pageType}/${id}`}><a className="feature-img" style={{ backgroundImage: "url(" + featuredImageUrl + ")" }}></a></Link>}
                <Link href={`/${pageType}/[id]`} as={`/${pageType}/${id}`}>
                    <a className="content">
                        <h4 className="title">{title}</h4>
                        <p className="summary">{summary}</p>
                    </a>
                </Link>
                <div className="date">{formatDate(updatedAt)}</div>
            </div>
        </PostItem>
    )
}

export default PostList;

const PostItem = styled.section`
    margin:1em 0;
    padding:0 0.6em;
    border-bottom: 1px solid #efefef;
    .post-item-wrapper{

        .feature-img{
            width:100%;
            padding-top:52%;
            position:relative;
            display:block;
            background-position:center;
            background-size:cover;
            border-radius:4px;
            overflow:hidden;
        }
        .content{
            display:block;
            padding:1.2em 0.4em;
            box-sizing:border-box;
            .title{
                color:#223141;
                font-weight:500;
                margin-bottom:0.4em;
                text-overflow:ellipsis;
                overflow:hidden;
                white-space:nowrap;
            }
            .summary{
                font-size:13px;
                color:#8794A3;

                line-height:1.4em;
                display:-webkit-box;
                -webkit-line-clamp:3;
                -webkit-box-orient:vertical;
                text-overflow:ellipsis;
                overflow:hidden;
                max-height:4.2em;
            }
        }

        .date{
            box-sizing:border-box;
            padding:1.2em 0.4em;
            font-size:13px;
            color:#8794A3;
        }
    }

`
